import React from 'react'
import '../sass/style.scss';
import '../sass/style_mobile.scss';

// right_grid
import Lottie from 'react-lottie';
import pig from '../img/pig.json';
import btc from '../img/BTC.json';
import cart from '../img/cart.json';
import card from '../img/creditcard.json';

// left_grid
import Group_1 from '../img/Group_1.svg'
import Group_2 from '../img/Group_2.svg'
import Group_3 from '../img/Group_3.svg'
import Group_4 from '../img/Group_4.svg'


import Linkedin from '../img/linkedin.svg'
import Mail from '../img/mail.svg'
import Github from '../img/github.svg'
import Instagram from '../img/instagram.svg'


function setupTabs (){
  document.querySelectorAll('.tab-btn').forEach(button=>{
    button.addEventListener('click',()=>{


      const sidebar = button.parentElement;
      const tabs = sidebar.parentElement;
      const tabNumber = button.dataset.forTab;
      const tabActivate = tabs.querySelector(`.tab-content[data-tab="${tabNumber}"]`)

      sidebar.querySelectorAll('.tab-btn').forEach(button=>{
        button.classList.remove('tab-btn-active')
      })
       tabs.querySelectorAll('.tab-content').forEach(tab=>{
        tab.classList.remove('tab-content-active')
      })
      button.classList.add('tab-btn-active')
      tabActivate.classList.add('tab-content-active')
    })
  })
}

document.addEventListener('DOMContentLoaded',()=>{
  setupTabs();
})

const animaton_pig = {
    loop: true,
    autoplay: true, 
    animationData: pig,
    rendererSettings: {
      preserveAspectRatio: 'xMidYMid slice'
    }
  };

  const animaton_btc_eth = {
    loop: true,
    autoplay: true, 
    animationData: btc,
    rendererSettings: {
      preserveAspectRatio: 'xMidYMid slice'
    }
  };
  const animaton_cart = {
    loop: true,
    autoplay: true, 
    animationData: cart,
    rendererSettings: {
      preserveAspectRatio: 'xMidYMid slice'
    }
  };
  const animaton_card = {
    loop: true,
    autoplay: true, 
    animationData: card,
    rendererSettings: {
      preserveAspectRatio: 'xMidYMid slice'
    }
  };



    class Grid extends React.Component {



          render(){
              return(
                <>
                    <div className="container_grid">

                    <div className="flexbox">
                    <div className="item">
                        <div className="content">
                        <Lottie options={animaton_cart}
                            height={120}
                            width={120}
                            />

                        <p className="p-description">Prazo de entrega</p>
                        <p className="p-description-1">Os melhores prazos para você que não quer esperar tanto pelo seu produto</p>
                        </div>
                    </div>
                    <div className="item">
                        <div className="content">
                        <Lottie options={animaton_pig}
                            height={120}
                            width={120}
                            />
                        <p className="p-description">Melhores descontos</p>
                        <p className="p-description-1">20% OFF, nas compras acima de 500 reais.</p> 
                        </div>
                    </div>
                    <div className="item">
                        <div className="content">
                        <Lottie options={animaton_card}
                            height={120}
                            width={120}
                            />
                        <p className="p-description">12x sem <br/> juros</p>
                        <p className="p-description-1">Parcele suas compras em todos os cartões.</p>
                        </div>
                    </div>
                    <div className="item">
                        <div className="content">
                        <Lottie options={animaton_btc_eth}
                            height={125}
                            width={125}
                            />
                        <p className="p-description">Quer mais descontos?</p>
                        <p className="p-description-1">Obtenha melhores descontos com pagamentos em criptomoedas.</p>
                        </div>
                    </div>
                    </div>

                    <div className="tabs">
        <div className="sidebar">
          {/* tabs buttons  */}
          <button className="tab-btn tab-btn-active" data-for-tab={1}>HTML</button>
          <button className="tab-btn" data-for-tab={2}>CSS</button>
          <button className="tab-btn" data-for-tab={3}>JavaScript</button>
        </div>
        {/* tabs content  */}
        <div className="content">
          <div className="tab-content tab-content-active" data-tab={1}>
            <img src="https://i.postimg.cc/vTkGR0jV/html5.png" alt="html logo" />
          </div>
          <div className="tab-content" data-tab={2}>
            <img src="https://i.postimg.cc/pXQjH7Rv/logo-2582747-960-720.png" alt="css logo" />
          </div>
          <div className="tab-content" data-tab={3}>
            <img src="https://i.postimg.cc/KY0SS938/js-logo.png" alt="js logo" />
          </div>
        </div>
      </div>

                    </div>

                </>
              )
          }
    }
    export default Grid
